import React from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; // Путь к вашей кастомной иконке маркера
import busStopIconImage from './bus.png'; // Путь к иконке остановки

class MapComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lat: 60.9394,
      lng: 76.5731,
      zoom: 13,
      routeNumber: null,
      routeData: [],
      sidebarVisible: false,
      filteredData: [],
      busStopData: [],
      routeTripsData: [],
      routeCoordinates: {}, // Добавляем состояние для хранения координат маршрутов
      selectedVector: null, // Добавляем состояние для хранения выбранного вектора
      selectedRoutes: [] // массив для хранения выбранных маршрутов
    };
  }

  componentDidMount() {
    this.fetchDataFromServer();
    this.fetchBusStopFromServer();
    this.fetchRouteTripsFromServer();
    this.interval = setInterval(() => {
      this.fetchDataFromServer();
    }, 6000);

    // Создаем и инициализируем busStopIcon
    this.busStopIcon = new L.Icon({
      iconUrl: busStopIconImage, // путь к вашей иконке остановки
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32],
      className: 'bus-stop-icon' // Добавляем класс иконке остановки
    });

    // Создаем и инициализируем customIcon
    this.customIcon = new L.Icon({
      iconUrl: customMarkerIcon,
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32],
      className: 'custom-icon' // Добавляем класс кастомной иконке
    });

    // Выбираем первый чекбокс при загрузке компонента
    const firstCheckbox = document.getElementById('checkbox1');
    if (firstCheckbox) {
      firstCheckbox.checked = true;
    }
  }

  componentWillUnmount() {
    clearInterval(this.interval);
  }

  handleCheckboxChange = (event) => {
    const { checked, id } = event.target;
    const routeId = parseInt(id.replace('checkbox', ''), 10);
    let updatedSelectedRoutes = [...this.state.selectedRoutes];

    if (checked) {
      // Если чекбокс отмечен, добавляем routeId в выбранные маршруты
      updatedSelectedRoutes.push(routeId);
      // Если выбран первый чекбокс, снимаем выбор с остальных
      if (routeId === 1) {
        for (let i = 2; i <= 4; i++) {
          const checkbox = document.getElementById(`checkbox${i}`);
          if (checkbox) {
            checkbox.checked = false;
          }
        }
      } else { // Если выбран любой другой чекбокс, снимаем выбор с первого
        const firstCheckbox = document.getElementById('checkbox1');
        if (firstCheckbox && firstCheckbox.checked) {
          firstCheckbox.checked = false;
        }
      }
    } else {
      // Если чекбокс снят, удаляем routeId из выбранных маршрутов
      updatedSelectedRoutes = updatedSelectedRoutes.filter(id => id !== routeId);
    }

    // Обновляем состояние selectedRoutes
    this.setState({ selectedRoutes: updatedSelectedRoutes });
  };

  render() {
    const center = [this.state.lat, this.state.lng];

    return (
      <div className="map-container">
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {this.state.sidebarVisible && (
            <div>
              <p>Это боковая панель</p>
              {/* Добавляем чекбокс */}
              <label className="sidebar-checkbox" htmlFor="checkbox1">
                <input id="checkbox1" type="checkbox" checked={true} onChange={this.handleCheckboxChange} />
                Все маршруты
              </label>
              <br />

              {/* Добавляем линию */}
              <div className="sidebar-line"></div>
              <br />
              {/* Добавляем еще три чекбокса */}
              <label className="sidebar-checkbox" htmlFor="checkbox2">
                <input id="checkbox2" type="checkbox" onChange={this.handleCheckboxChange} />
                6
              </label>
              <br />
              <label className="sidebar-checkbox" htmlFor="checkbox3">
                <input id="checkbox3" type="checkbox" onChange={this.handleCheckboxChange} />
                7
              </label>
              <br />
              <label className="sidebar-checkbox" htmlFor="checkbox4">
                <input id="checkbox4" type="checkbox" onChange={this.handleCheckboxChange} />
                16
              </label>
            </div>
          )}
        </div>
        <MapContainer zoom={this.state.zoom} center={center} zoomControl={false}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            // OpenStreetMap attribution continues
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {/* Добавляем маркеры с всплывающими окнами (Popup) для данных об остановках */}
          {this.state.busStopData.map((stop, index) => (
            <Marker
              key={index}
              position={[stop.BUSSTOP_LATITUDE, stop.BUSSTOP_LONGITUDE]}
              icon={this.busStopIcon}
              zIndexOffset={100} // Смещаем zIndex для иконок остановок
            >
              <Popup closeButton={false}>
                <div>
                  <p>ID остановки: {stop.BUSSTOP_ID}</p>
                  <p>Название остановки: {stop.BUSSTOP_NAME}</p>
                  <p>Широта: {stop.BUSSTOP_LATITUDE}</p>
                  <p>Долгота: {stop.BUSSTOP_LONGITUDE}</p>
                </div>
              </Popup>
            </Marker>
          ))}
          {/* Добавляем маркеры с всплывающими окнами (Popup) для отфильтрованных данных */}
          {this.state.filteredData.map((item, index) => {
            // Проверяем, нужно ли отобразить данный маркер
            const shouldDisplayMarker = this.state.selectedRoutes.includes(item.ROUTE_ID) || this.state.selectedRoutes.length === 0;

            if (shouldDisplayMarker) {
              return (
                <Marker
                  key={index}
                  position={[item.LATITUDE, item.LONGITUDE]}
                  icon={this.customIcon}
                  zIndexOffset={500}
                  eventHandlers={{
                    click: () => {
                      // Получаем координаты маршрута по выбранному ROUTE_TRIP_ID
                      const selectedRouteCoordinates = this.state.routeCoordinates[item.ROUTE_TRIP_ID];
                      // Проверяем, был ли уже выбран этот маршрут
                      if (this.state.routeNumber === item.ROUTE_TRIP_ID) {
                        // Если да, то скрываем вектор
                        this.setState({
                          selectedVector: null,
                          routeNumber: null
                        });
                      } else {
                        // Если нет, то обновляем выбранный векторный маршрут и маршрутный номер
                        this.setState({
                          selectedVector: (
                            <Polyline
                              positions={selectedRouteCoordinates}
                              color="red"
                            />
                          ),
                          routeNumber: item.ROUTE_TRIP_ID
                        });
                      }
                    }
                  }}
                >
                  <Popup closeButton={false} bubblingMouseEvents={true}>
                    <div>
                      <p>Широта: {item.LATITUDE}</p>
                      <p>Долгота: {item.LONGITUDE}</p>
                      <p>Скорость: {item.SPEED}</p>
                      <p>Route Trip ID: {item.ROUTE_TRIP_ID}</p>
                      <p>Route  ID: {item.ROUTE_ID}</p>
                    </div>
                  </Popup>
                </Marker>
              );
            } else {
              return null;
            }
          })}
          {/* Добавляем выбранный вектор */}
          {this.state.selectedVector}
        </MapContainer>
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }}
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
  }
}

export default MapComponent;
