import React from "react";
import { MapContainer, TileLayer, Marker, Popup, ZoomControl } from 'react-leaflet';
import L from 'leaflet'; // Добавим импорт из Leaflet
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; // Путь к вашей кастомной иконке маркера

class MapComponent extends React.Component {
  constructor(props) {
    super(props);
    this.mapRef = React.createRef(); // Инициализация mapRef
  }

  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
    showMarkers: true // состояние видимости маркеров
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  handleMarkerClick = () => {
    const map = this.mapRef.current; // Получаем объект карты из ref
    console.log("Текущий zoom:", map.getZoom());
  };

  handleZoomStart = () => {
    const map = this.mapRef.current; // Получаем объект карты из ref
    const currentZoom = map.getZoom();
    // Определяем, должны ли маркеры быть видимыми или скрытыми
    if (currentZoom < 12) {
      this.setState({ showMarkers: false });
    } else {
      this.setState({ showMarkers: true });
    }
  };

  handleZoomIn = () => {
    this.setState((prevState) => ({
      zoom: prevState.zoom + 1
    }));
  };

  handleZoomOut = () => {
    this.setState((prevState) => ({
      zoom: prevState.zoom - 1
    }));
  };

  render() {
    const center = [this.state.lat, this.state.lng];

    // Создаем объект иконки
    const customIcon = new L.Icon({
      iconUrl: customMarkerIcon, // Путь к вашей кастомной иконке
      iconSize: [32, 32], // Размеры иконки
      iconAnchor: [16, 32], // Анкор иконки (точка, относительно которой она позиционируется)
      popupAnchor: [0, -32], // Анкор для всплывающего окна
    });

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {this.state.sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer
          zoom={this.state.zoom}
          center={center}
          zoomControl={false}
          ref={this.mapRef} // Добавляем ref к MapContainer
          onzoomstart={this.handleZoomStart} // Слушаем событие начала изменения масштаба
        >
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Показываем маркер только если showMarkers === true */}
          {this.state.showMarkers && (
            <Marker position={[60.9394, 76.5731]} icon={customIcon} eventHandlers={{ click: this.handleMarkerClick }}>
              <Popup closeButton={false}>
                фпа
              </Popup>
            </Marker>
          )}

          <ZoomControl position="bottomright" />

        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <div className="zoom-buttons">
          <button className="zoom-in" onClick={this.handleZoomIn}>+</button>
          <button className="zoom-out" onClick={this.handleZoomOut}>-</button>
        </div>

        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }}
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
  }
}

export default MapComponent;
