import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // Добавим импорт из Leaflet
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; // Путь к вашей кастомной иконке

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
    markersVisible: true, // состояние видимости маркеров
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  handleMapZoom = () => {
    // Обработчик события для изменения масштаба карты
    // Здесь вы можете скрывать маркеры при необходимости
    if (this.map && this.map.getZoom() < 10) { // Определяем пороговый уровень масштабирования
      this.setState({
        markersVisible: false,
      });
    } else {
      this.setState({
        markersVisible: true,
      });
    }
  };

  render() {
    const center = [this.state.lat, this.state.lng];

    // Создаем объект иконки
    const customIcon = new L.Icon({
      iconUrl: customMarkerIcon, // Путь к вашей кастомной иконке
      iconSize: [32, 32], // Размеры иконки
      iconAnchor: [16, 32], // Анкор иконки (точка, относительно которой она позиционируется)
      popupAnchor: [0, -32], // Анкор для всплывающего окна
    });

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {this.state.sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer
          zoom={this.state.zoom}
          center={center}
          zoomControl={false}
          onZoomend={this.handleMapZoom}
          whenCreated={(map) => (this.map = map)} // Сохраняем ссылку на объект карты
        >
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Добавляем маркер с всплывающим окном (Popup) */}
          {this.state.markersVisible && (
            <Marker position={[60.9394, 76.5731]} icon={customIcon}>
              <Popup closeButton={false}>
                фпа
              </Popup>
            </Marker>
          )}
        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }}
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
  }
}

export default MapComponent;
