import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // Импортируем Leaflet
import 'leaflet/dist/leaflet.css'; // Подключаем стили Leaflet
import './Map.css';

// Импортируем изображение вашего пина
import customIcon from './customIcon.png';

// Устанавливаем иконку с помощью Leaflet
const customMarkerIcon = L.icon({
  iconUrl: customIcon,
  iconSize: [40, 40], // Размеры вашего изображения пина
  iconAnchor: [20, 40], // Расположение "якоря" вашей иконки
});

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  render() {
    const center = [this.state.lat, this.state.lng];

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {this.state.sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer zoom={this.state.zoom} center={center} zoomControl={false}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Добавляем маркер с пользовательской иконкой */}
          <Marker position={[60.9394, 76.5731]} icon={customMarkerIcon}>
            {/* Можно добавить всплывающее окно (Popup) с информацией */}
            <Popup>
              <div>
                <h2>Здесь что-то важное</h2>
                <p>Ваш текст или информация</p>
              </div>
            </Popup>
          </Marker>
        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }} // Перемещаем кнопку в зависимости от состояния боковой панели
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
  }
}

export default MapComponent;
