// Ваш файл Map1.js (React)
import React from "react";
import { MapContainer, TileLayer } from 'react-leaflet';
import './Map.css';

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
  };

  handleRouteRequest = () => {
    fetch(`http://localhost:3002/routes/${this.state.routeNumber}`)
      .then(response => response.json())
      .then(data => {
        this.setState({ routeData: data });
      })
      .catch(error => console.error('Error fetching route data:', error));
  };

  render() {
    var center = [this.state.lat, this.state.lng];

    return (
      <div>
        <input type="number" value={this.state.routeNumber} onChange={(e) => this.setState({ routeNumber: e.target.value })} />
        <button onClick={this.handleRouteRequest}>Запросить</button>
        <MapContainer zoom={this.state.zoom} center={center}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {/* Здесь добавьте код для отображения маршрутов на карте */}
        </MapContainer>
      </div>
    );
  }
}

export default MapComponent;
