import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; 
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; 

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    stopVisible: true // По умолчанию показываем остановку
  };

  handleViewportChange = (viewport) => {
    const { zoom } = viewport;
    this.setState({ stopVisible: zoom >= 10 });
  };

  render() {
    const { lat, lng, zoom, stopVisible } = this.state;
    const center = [lat, lng];
    const customIcon = new L.Icon({
      iconUrl: customMarkerIcon,
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32],
    });

    return (
      <MapContainer
        zoom={zoom}
        center={center}
        zoomControl={false}
        onViewportChange={this.handleViewportChange}
      >
        <TileLayer
          attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />

        {stopVisible && ( // Условие отображения остановки
          <Marker position={[60.9394, 76.5731]} icon={customIcon}>
            <Popup closeButton={false}>
              фпа
            </Popup>
          </Marker>
        )}
      </MapContainer>
    );
  }
}

export default MapComponent;
