import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import './Map.css';
import customMarkerIcon from './bus.png.png'; // Путь к вашей кастомной иконке маркера

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  render() {
    const center = [this.state.lat, this.state.lng];

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {this.state.sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer zoom={this.state.zoom} center={center} zoomControl={false}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Добавляем маркер с всплывающим окном (Popup) */}
          <Marker position={[60.9394, 76.5731]}>
            <Popup minWidth={200} closeButton={false}> {/* Устанавливаем минимальную ширину Popup */}
              <img src={customMarkerIcon} alt="Custom Marker" style={{ maxWidth: "100%" }} /> {/* Вставляем вашу кастомную иконку */}
              <p>Тут может быть текст</p> {/* Добавляем нужный текст */}
            </Popup>
          </Marker>
        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }} // Перемещаем кнопку в зависимости от состояния боковой панели
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
  }
}

export default MapComponent;
