import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; // Добавим импорт из Leaflet
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; // Путь к вашей кастомной иконке

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
    markers: [ // список маркеров
      {
        position: [60.9394, 76.5731],
        popupContent: "фпа"
      }
    ],
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  handleMapZoom = (e) => {
    // Обработчик события для изменения масштаба карты
    // Здесь вы можете добавлять и удалять маркеры при отдалении и приближении карты
    const currentZoom = e.target.getZoom();
    const { markers } = this.state;
    if (currentZoom >= 13) {
      // При достаточном уровне масштабирования добавляем новый маркер
      const newMarkers = [
        ...markers,
        {
          position: [61, 71],
          popupContent: "Новый маркер"
        }
      ];
      this.setState({
        markers: newMarkers,
      });
    } else {
      // При недостаточном уровне масштабирования удаляем последний маркер
      const newMarkers = markers.slice(0, markers.length - 1);
      this.setState({
        markers: newMarkers,
      });
    }
  };

  render() {
    const { lat, lng, zoom, markers, sidebarVisible } = this.state;
    const center = [lat, lng];

    // Создаем объект иконки
    const customIcon = new L.Icon({
      iconUrl: customMarkerIcon, // Путь к вашей кастомной иконке
      iconSize: [32, 32], // Размеры иконки
      iconAnchor: [16, 32], // Анкор иконки (точка, относительно которой она позиционируется)
      popupAnchor: [0, -32], // Анкор для всплывающего окна
    });

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer zoom={zoom} center={center} zoomControl={false} onZoomend={this.handleMapZoom}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Отображаем маркеры */}
          {markers.map((marker, index) => (
            <Marker key={index} position={marker.position} icon={customIcon}>
              <Popup closeButton={false}>
                {marker.popupContent}
              </Popup>
            </Marker>
          ))}
        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: sidebarVisible ? '210px' : '10px' }}
        >
          {sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
  }
}

export default MapComponent;
