import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import L from 'leaflet'; 
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; 

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false,
    stopVisible: true // По умолчанию показываем остановку
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  handleZoomEnd = (e) => {
    const newZoom = e.target.getZoom();
    console.log("Zoom level:", newZoom); // Выводим текущий уровень зума в консоль
    // Обновляем видимость остановки в зависимости от уровня масштабирования
    this.setState({ stopVisible: newZoom >= 10 });
  };
  
  

  render() {
    
    const { lat, lng, zoom, stopVisible } = this.state;
    const center = [lat, lng];
    const customIcon = new L.Icon({
      iconUrl: customMarkerIcon,
      iconSize: [32, 32],
      iconAnchor: [16, 32],
      popupAnchor: [0, -32],
    });

    return (
      
      <div className="map-container">
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {this.state.sidebarVisible && (
            <div>
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        <MapContainer
          zoom={zoom}
          center={center}
          zoomControl={false}
          onZoomEnd={this.handleZoomEnd}
        >
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {stopVisible && ( // Условие отображения остановки
            <Marker position={[60.9394, 76.5731]} icon={customIcon}>
              <Popup closeButton={false}>
                фпа
              </Popup>
            </Marker>
          )}
        </MapContainer>

        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }}
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>
      </div>
    );
    console.log("stopVisible:", stopVisible); // добавим эту строку в метод render
  }
}

export default MapComponent;
