import React from "react";
import { MapContainer, TileLayer, MapControl } from 'react-leaflet';
import './Map.css';

class SidebarControl extends MapControl {
  createLeafletElement() {}
  
  componentDidMount() {
    const div = L.DomUtil.create('div', 'leaflet-control leaflet-control-custom');
    div.innerHTML = '<button class="sidebar-toggle-btn">Toggle Sidebar</button>';
    div.onclick = this.props.onClick;
    return div;
  }
}

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  render() {
    var center = [this.state.lat, this.state.lng];

    return (
      <div className="map-container">
        {/* Карта */}
        <MapContainer zoom={this.state.zoom} center={center}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {/* Кнопка для открытия/закрытия боковой панели */}
          <SidebarControl position="topleft" onClick={this.toggleSidebar} />
        </MapContainer>
      </div>
    );
  }
}

export default MapComponent;
