import React from "react";
import { MapContainer, TileLayer, Marker, Popup, Polyline } from 'react-leaflet';
import L from 'leaflet';
import './Map.css';
import customMarkerIcon from './customMarkerIcon.png'; // Путь к вашей кастомной иконке маркера
import routeData from './GetRouteTripsForIntegration.json'; // Импортируем JSON с данными о маршрутах

class MapComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      lat: 60.9394,
      lng: 76.5731,
      zoom: 13,
      routeNumber: null,
      routeData: [],
      sidebarVisible: false, // состояние видимости боковой панели
      vectorVisible: false, // состояние видимости вектора
      vectorCoordinates: [], // координаты для вектора
    };
    this.handleMarkerClick = this.handleMarkerClick.bind(this);
    this.toggleVector = this.toggleVector.bind(this);
    this.handleZoomEnd = this.handleZoomEnd.bind(this);
    this.fetchRouteData = this.fetchRouteData.bind(this);
  }

  componentDidMount() {
    // загрузка данных о маршруте при монтировании компонента
    this.fetchRouteData();
  }

  fetchRouteData() {
    const getRouteById = {};

    // Чтение данных о маршрутах из JSON файла и формирование объекта с данными о маршрутах
    routeData.forEach(route => {
      if (getRouteById[route["ROUTE_TRIP_ID"]]) {
          getRouteById[route["ROUTE_TRIP_ID"]].push([route["POINT_LATITUDE"], route["POINT_LONGITUDE"]]);
      } else {
          getRouteById[route["ROUTE_TRIP_ID"]] = [[route["POINT_LATITUDE"], route["POINT_LONGITUDE"]]];
      }
    });

    this.setState({ routeData: getRouteById });
  }

  handleMarkerClick = () => {
    this.setState((prevState) => ({
      vectorVisible: !prevState.vectorVisible,
    }));
  };

  toggleVector() {
    this.setState((prevState) => ({
      vectorVisible: !prevState.vectorVisible,
    }));
  }

  handleZoomEnd(e) {
    this.setState({
      zoom: e.target._zoom,
    });
  }

  render() {
    const center = [this.state.lat, this.state.lng];

    // Создаем объект иконки
    const customIcon = new L.Icon({
      iconUrl: customMarkerIcon, // Путь к вашей кастомной иконке
      iconSize: [32, 32], // Размеры иконки
      iconAnchor: [16, 32], // Анкор иконки (точка, относительно которой она позиционируется)
      popupAnchor: [0, -32], // Анкор для всплывающего окна
    });

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${this.state.sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {this.state.sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer
          zoom={this.state.zoom}
          center={center}
          zoomControl={false}
          attributionControl={false}
          onZoomEnd={this.handleZoomEnd} // Обработчик изменения уровня масштабирования
        >
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />

          {/* Добавляем маркер с всплывающим окном (Popup) */}
          {this.state.zoom >= 12 && (
            <Marker position={[60.9394, 76.5731]} icon={customIcon} eventHandlers={{ click: this.handleMarkerClick }}>
              <Popup closeButton={false}>
                фпа
              </Popup>
            </Marker>
          )}

          {/* Отображаем вектор, если он видим */}
          {this.state.vectorVisible && <Polyline positions={vectorCoordinates} />}

        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: this.state.sidebarVisible ? '210px' : '10px' }}
        >
          {this.state.sidebarVisible ? '<' : '>'}
        </button>

        {/* Кнопка для показа/скрытия вектора */}
        <button
          className="vector-toggle-btn"
          onClick={this.toggleVector}
        >
          Показать/скрыть вектор
        </button>
      </div>
    );
  }
}

export default MapComponent;
