import React from "react";
import { MapContainer, TileLayer } from 'react-leaflet';
import './Map.css';

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    sidebarVisible: false, // состояние видимости боковой панели
    nightMode: false, // состояние ночного режима
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  toggleNightMode = () => {
    this.setState((prevState) => ({
      nightMode: !prevState.nightMode,
    }));
  };

  render() {
    const { lat, lng, zoom, sidebarVisible, nightMode } = this.state;
    const center = [lat, lng];
    const tileLayerUrl = nightMode ? "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" : "https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png";

    return (
      <div className="map-container">
        {/* Боковая панель */}
        <div className={`sidebar ${sidebarVisible ? 'visible' : ''}`}>
          {/* Содержимое боковой панели */}
          {sidebarVisible && (
            <div>
              {/* Содержимое боковой панели */}
              <p>Это боковая панель</p>
            </div>
          )}
        </div>

        {/* Карта */}
        <MapContainer zoom={zoom} center={center} className="map">
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url={tileLayerUrl}
            maxZoom={18}
            minZoom={10}
            zoomControl={false} // Отключаем стандартные кнопки zoom
          />
        </MapContainer>

        {/* Кнопка для открытия/закрытия боковой панели */}
        <button
          className="sidebar-toggle-btn"
          onClick={this.toggleSidebar}
          style={{ left: sidebarVisible ? '210px' : '10px' }}
        >
          {sidebarVisible ? '<' : '>'}
        </button>

        {/* Кнопка для смены темы */}
        <button
          className="theme-toggle-btn"
          onClick={this.toggleNightMode}
          style={{ top: '10px', right: '10px' }}
        >
          Switch to {nightMode ? 'Day' : 'Night'} Mode
        </button>
      </div>
    );
  }
}

export default MapComponent;
