import React from "react";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import './Map.css';

class MapComponent extends React.Component {
  state = {
    lat: 60.9394,
    lng: 76.5731,
    zoom: 13,
    routeNumber: null,
    routeData: [],
    checkboxes: Array.from({ length: 10 }, (_, i) => i + 1), // массив чекбоксов от 1 до 10
    sidebarVisible: false, // состояние видимости боковой панели
  };

  toggleSidebar = () => {
    this.setState((prevState) => ({
      sidebarVisible: !prevState.sidebarVisible,
    }));
  };

  render() {
    var center = [this.state.lat, this.state.lng];

    return (
      <div className="map-container">
        <button onClick={this.toggleSidebar}>Toggle Sidebar</button>
        {this.state.sidebarVisible && (
          <div className="sidebar">
            {this.state.checkboxes.map((number) => (
              <label key={number}>
                <input type="checkbox" value={number} />
                Route {number}
              </label>
            ))}
          </div>
        )}
        <MapContainer zoom={this.state.zoom} center={center}>
          <TileLayer
            attribution='&amp;copy <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          />
          {/* Место для маркеров */}
        </MapContainer>
      </div>
    );
  }
}

export default MapComponent;
